template &lt;typename T&gt;
class Optional
{
    T value;
    bool valid;

public:

    // for the if(var) test
    operator bool() const  {  return valid;  }

    //  for assigning a value
    Optional&lt;T&gt; &amp;operator=(T rhs)   
    {  
        value = rhs;  
        valid = true;  
        return *this;  
    }

    //  for assigning "empty"
    Optional&lt;T&gt; &amp;operator=(void *)  
    {  
        valid = false;  
        return *this;  
    }

