function binaryClosestIndexOf(array, value) {
  var mid,
    lo = 0,
    hi = array.length - 1;

  while (hi - lo &gt; 1) {
    mid = (lo + hi) &gt;&gt;&gt; 1;

    if (array[mid] &gt; value)
      hi = mid;
    else
      lo = mid;
  }

  if (value - array[lo] &lt;= array[hi] - value)
    return lo;
  else 
    return hi;
}


