use std::fmt::Debug;
use std::marker::PhantomData;
use std::sync::Arc;
use std::thread;

#[derive(Debug)]
struct Region {
    width: usize,
    height: usize,
    // ... more stuff that would be read from a file
}

#[derive(Debug)]
struct Map&lt;T&gt; {
    filename: String,
    phantom: PhantomData&lt;*const T&gt;,
}

// General Map methods
impl&lt;T&gt; Map&lt;T&gt;
where
    T: Debug,
{
    pub fn new&lt;S&gt;(filename: S) -&gt; Self
    where
        S: Into&lt;String&gt;,
    {
        Map {
            filename: filename.into(),
            phantom: PhantomData,
        }
    }

    pub fn do_stuff(&amp;self) {
        println!("doing stuff {:?}", self);
    }
}

// Methods specific to Map&lt;Region&gt;
impl Map&lt;Region&gt; {
    pub fn get_region(&amp;self) -&gt; Region {
        Region {
            width: 10,
            height: 20,
        }
    }
}

fn main() {
    let master_map = Arc::new(Map::&lt;Region&gt;::new("mapfile"));
    master_map.do_stuff();
    let region = master_map.get_region();
    println!("{:?}", region);

    let join_handle = {
        let map = Arc::clone(&amp;master_map);
        thread::spawn(move || {
            println!("In subthread...");
            map.do_stuff();
        })
    };

    join_handle.join().unwrap();
}


