class Foo {
    public:
    Foo() {
        std::cout &lt;&lt; "created\n";
        }
    ~Foo() {
        std::cout &lt;&lt; "destroyed  " &lt;&lt; value &lt;&lt; "\n";
    }
    Foo(const Foo&amp; other) : value(other.value) {
        std::cout &lt;&lt; "copied\n";
    }
    Foo(Foo&amp;&amp; other) : value(other.value) {
        other.value++;
        std::cout &lt;&lt; "moved\n";
    }
    Foo(const Foo&amp;&amp; other) : value(other.value) {
        std::cout &lt;&lt; "const-moved\n";
    }    
    int value = 0;
};

