    int length = 5;


    int *numbers[length];

    for (int i = 0; i < length; i++) {
        numbers[i] = malloc(sizeof(int));
        if (!numbers[i]) {
            perror("malloc");
            exit(1);
        }
        *numbers[i] = i * 10;
    }
     for (int i = 0; i < length; i++) {
        printf("numbers[%d] = %d (address=%p)\n", i, *numbers[i], (void*)numbers[i]); // It works
